public function getTopCustomers(){

	$data = array();

	$data['total'] = array();
	$data['name'] = array();
	// Set the timeframe
	if (isset($this->request->get['range'])) {
		$range = $this->request->get['range'];
	} else {
		$range = 'month';
	}

	$day_of_year = date("z") + 1;
	$today_date = date("Y-m-d H:i:s");
	$beginning_of_today = date("Y-m-d 00:00:00");
	$first_day_of_month = date("Y-m-1");
	$first_day_of_year = date("Y-1-1");

	function firstDayOfWeek(){
		$today_nth_day = date("w");
		$first_day_of_week_mktime = mktime(0, 0, 0, date("m"), date("d") - $today_nth_day, date("Y"));
		$first_day_of_week = date("Y-m-d", $first_day_of_week_mktime);
		return $first_day_of_week;
	}

	$first_day_of_week = firstDayOfWeek();

	// Collect data based on the timeframe selected (Day, Week, Month, Year)
	switch ($range) {
		case 'day':
		$sql = "SELECT SUM(total) AS total, store_id, CONCAT(firstname, ' ', lastname) AS name, customer_id FROM `" . DB_PREFIX . "order` WHERE store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $beginning_of_today . "' AND '" . $today_date . "' GROUP BY customer_id ORDER BY SUM(total) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['name'][] = $row['name'];
				$data['total'][] = $row['total'];
				$data['total_currency'][] = $this->currency->format($row['total'], $this->config->get('config_currency'));
			}
		}else{
			$data['name'][] = "";
			$data['total'][] = "";
			$data['total_currency'][] = "";
		}
		break;


		case 'week':
		$sql = "SELECT SUM(total) AS total, store_id, CONCAT(firstname, ' ', lastname) AS name, customer_id FROM `" . DB_PREFIX . "order` WHERE store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_week . "' AND '" . $today_date . "' GROUP BY customer_id ORDER BY SUM(total) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['name'][] = $row['name'];
				$data['total'][] = $row['total'];
				$data['total_currency'][] = $this->currency->format($row['total'], $this->config->get('config_currency'));
			}
		}else{
			$data['name'][] = "";
			$data['total'][] = "";
			$data['total_currency'][] = "";
		}
		break;

		default:
		case 'month':
		$sql = "SELECT SUM(total) AS total, store_id, CONCAT(firstname, ' ', lastname) AS name, customer_id FROM `" . DB_PREFIX . "order` WHERE store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_month . "' AND '" . $today_date . "' GROUP BY customer_id ORDER BY SUM(total) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['name'][] = $row['name'];
				$data['total'][] = $row['total'];
				$data['total_currency'][] = $this->currency->format($row['total'], $this->config->get('config_currency'));
			}
		}else{
			$data['name'][] = "";
			$data['total'][] = "";
			$data['total_currency'][] = "";
		}
		break;

		case 'year':
		$sql = "SELECT SUM(total) AS total, store_id, CONCAT(firstname, ' ', lastname) AS name, customer_id FROM `" . DB_PREFIX . "order` WHERE store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_year . "' AND '" . $today_date . "' GROUP BY customer_id ORDER BY SUM(total) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['name'][] = $row['name'];
				$data['total'][] = $row['total'];
				$data['total_currency'][] = $this->currency->format($row['total'], $this->config->get('config_currency'));
			}
		}else{
			$data['name'][] = "";
			$data['total'][] = "";
			$data['total_currency'][] = "";
		}
		break;
	}

	$this->response->setOutput(json_encode($data));
}

public function getTopProducts(){

	$data = array();

	$data['product_name'] = array();
	$data['quantity'] = array();
	// Set the timeframe
	if (isset($this->request->get['range'])) {
		$range = $this->request->get['range'];
	} else {
		$range = 'month';
	}

	$day_of_year = date("z") + 1;
	$today_date = date("Y-m-d H:i:s");
	$beginning_of_today = date("Y-m-d 00:00:00");
	$first_day_of_month = date("Y-m-1");
	$first_day_of_year = date("Y-1-1");

	function firstDayOfWeek(){
		$today_nth_day = date("w");
		$first_day_of_week_mktime = mktime(0, 0, 0, date("m"), date("d") - $today_nth_day, date("Y"));
		$first_day_of_week = date("Y-m-d", $first_day_of_week_mktime);
		return $first_day_of_week;
	}

	$first_day_of_week = firstDayOfWeek();

	// Collect data based on the timeframe selected (Day, Week, Month, Year)
	switch ($range) {
		case 'day':
		$sql = "SELECT SUM(op.quantity) AS quantity, op.name AS product_name, op.product_id AS product_id FROM `" . DB_PREFIX . "order_product` AS op JOIN `" . DB_PREFIX . "order` AS o ON (op.order_id = o.order_id) WHERE o.store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $beginning_of_today . "' AND '" . $today_date . "' GROUP BY op.name ORDER BY SUM(op.quantity) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['product_name'][] = $row['product_name'];
				$data['quantity'][] = $row['quantity'];
			}
		}else{
			$data['product_name'][] = "";
			$data['quantity'][] = "";
		}
		break;


		case 'week':
		$sql = "SELECT SUM(op.quantity) AS quantity, op.name AS product_name, op.product_id AS product_id FROM `" . DB_PREFIX . "order_product` AS op JOIN `" . DB_PREFIX . "order` AS o ON (op.order_id = o.order_id) WHERE o.store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_week . "' AND '" . $today_date . "' GROUP BY op.name ORDER BY SUM(op.quantity) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['product_name'][] = $row['product_name'];
				$data['quantity'][] = $row['quantity'];
			}
		}else{
			$data['product_name'][] = "";
			$data['quantity'][] = "";
		}
		break;

		default:
		case 'month':
		$sql = "SELECT SUM(op.quantity) AS quantity, op.name AS product_name, op.product_id AS product_id FROM `" . DB_PREFIX . "order_product` AS op JOIN `" . DB_PREFIX . "order` AS o ON (op.order_id = o.order_id) WHERE o.store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_month . "' AND '" . $today_date . "' GROUP BY op.name ORDER BY SUM(op.quantity) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
				$data['product_name'][] = $row['product_name'];
				$data['quantity'][] = $row['quantity'];
			}
		}else{
			$data['product_name'][] = "";
			$data['quantity'][] = "";
		}
		break;

		case 'year':
		$sql = "SELECT SUM(op.quantity) AS quantity, op.name AS product_name, op.product_id AS product_id FROM `" . DB_PREFIX . "order_product` AS op JOIN `" . DB_PREFIX . "order` AS o ON (op.order_id = o.order_id) WHERE o.store_id = '" . $this->config->get("config_store_id") . "' AND date_added BETWEEN '" . $first_day_of_year . "' AND '" . $today_date . "' GROUP BY op.name ORDER BY SUM(op.quantity) DESC LIMIT 5";

		$query = $this->db->query($sql);

		if($query->num_rows){
			$rows =  $query->rows;
			foreach($rows as $row){
