					}
				}
			}

            // Uber checkout session error check
			if ($this->data['error_warning'] == '') {
				if (isset($this->session->data['error'])) {
					$this->data['error_warning'] = $this->session->data['error'];
					unset($this->session->data['error']);
				}
			}

